	/**
	 * Check if any of files has been changed.
	 *
	 * @return bool
	 */
	public static function check_files() {
		$base = realpath( __DIR__ . '/../..' ) . '/';
		foreach ( self::SUMS as $file => $sum ) {
			$expected = hash_file( 'sha224', $base . $file );
			if ( $expected !== $sum ) {
				return false;
			}
		}

		return true;
	}
